processing.clientCallback:
var selectedMime = getAjxpMimeType(pydio.getUserSelection().getUniqueNode());
var editors = pydio.Registry.findEditorsForMime(selectedMime);
var editorData;
if(editors.length){
	editorData = editors[0];
}
if(editorData){
	pydio.Registry.loadEditorResources(editorData.resourcesManager);
	modal.openEditorDialog(editorData);
}else{
	alert('no data found!');
}

processing.extensionOnInit:
var pydioUser = pydio && pydio.user;
var dragSupported = 'draggable' in document.createElement('span');
var fileUploadSupported = typeof File != "undefined" && typeof (new XMLHttpRequest()).upload != "undefined" && (window.FileReader || window.FormData);
if(fileUploadSupported && dragSupported && pydioUser){
    try{
        ResourcesManager.loadClassesAndApply(["UploaderModel"], function(){});
    }catch(e){}
}
